#!/usr/bin/env node
import mri from 'mri';
import { resolve } from 'pathe';
import { a as createNitro, f as createDevServer, p as prepare, b as build, c as copyPublicAssets, h as prerender } from './chunks/prerender.mjs';
import 'worker_threads';
import 'fs';
import 'chokidar';
import 'perfect-debounce';
import 'h3';
import 'http-proxy';
import 'listhen';
import 'serve-static';
import 'connect';
import 'ufo';
import 'hookable';
import 'unimport';
import 'consola';
import 'c12';
import 'klona/full';
import 'defu';
import 'module';
import 'fs-extra';
import 'jiti';
import 'chalk';
import 'dot-prop';
import 'url';
import 'archiver';
import 'globby';
import 'pkg-types';
import 'rollup';
import 'pretty-bytes';
import 'gzip-size';
import 'std-env';
import '@nuxt/devalue';
import 'rollup-plugin-terser';
import '@rollup/plugin-commonjs';
import '@rollup/plugin-node-resolve';
import '@rollup/plugin-alias';
import '@rollup/plugin-json';
import '@rollup/plugin-replace';
import '@rollup/plugin-virtual';
import '@rollup/plugin-wasm';
import '@rollup/plugin-inject';
import 'rollup-plugin-visualizer';
import 'unenv';
import 'mlly';
import 'unimport/unplugin';
import '@vercel/nft';
import 'semver';
import 'etag';
import 'mime';
import 'hasha';
import 'table';
import 'is-primitive';
import 'esbuild';
import '@rollup/pluginutils';

async function main() {
  const args = mri(process.argv.slice(2));
  const command = args._[0];
  const rootDir = resolve(args._[1] || ".");
  if (command === "dev") {
    const nitro = await createNitro({
      rootDir,
      dev: true,
      preset: "dev"
    });
    const server = createDevServer(nitro);
    await server.listen({});
    await prepare(nitro);
    await build(nitro);
    return;
  }
  if (command === "build") {
    const nitro = await createNitro({
      rootDir,
      dev: false
    });
    await prepare(nitro);
    await copyPublicAssets(nitro);
    await build(nitro);
    await prerender(nitro);
    process.exit(0);
  }
  console.error(`Unknown command ${command}! Usage: nitro dev|build [rootDir]`);
  process.exit(1);
}
main().catch((err) => {
  console.error(err);
  process.exit(1);
});
